//LEFT LIST
var left_list = d3.select("#left-trolls").append("svg")
    .attr("width", width)
    .attr("height", height)
    

//RIGHT LIST
var right_list = d3.select("#right-trolls").append("svg")
    .attr("width", width)
    .attr("height", height)

var date = d3.select("#date").append("text")

var LL = left_list.append("g")
var RL = right_list.append("g")

var L_color_scale = d3.scaleLinear().domain([10, 300]).range(['blue', 'darkblue'])
var R_color_scale = d3.scaleLinear().domain([10, 3000]).range(['red', 'darkred'])

//ANIMATION
var month = 0;

//render first frame
renderWords(0);

function increaseMonth() {
	if (month < 29) {
		clearWords()
		month++
		renderWords(month)
	}
}

function decreaseMonth() {
	if (month > 0) {
		clearWords()
		month --
		renderWords(month)
	}
}

function clearWords() {
	LL.selectAll("*").remove()
	RL.selectAll("*").remove()
}

function renderWords(month) {

	//LEFT KEY FRAMES
	for(var i in leftKeyFrames[month][1].slice(0, 10)) {
		// console.log("curr value:", leftKeyFrames[month][1][i])
		// console.log("previous value:", prev.get(leftKeyFrames[month][1][i]))
	    var value = leftKeyFrames[month][1][i].value;
	    LL.append("text")
	    .attr("x", 280)
	    .attr("y", (i * 50) + 50)
	    .attr("font-family", "Georgia")
	    .attr("font-size", function(){
	    	var size = (Math.log(value) / Math.log(1.175));
	    	
	    	if (size < 14) {
	    		size = 14;
	    	}
	        return (size + "px") 
	    })
	    .text(leftKeyFrames[month][1][i].word)
	    .attr("fill", function(){
	        return L_color_scale(value)
	    })
	    .attr("text-anchor", "middle")

	}

	//RIGHT KEY FRAMES
	for(i in rightKeyFrames[month][1].slice(0, 10)) {
	    var value = rightKeyFrames[month][1][i].value;
	    RL.append("text")
	    .attr("x", 280)
	    .attr("y", (i * 50) + 50)
	    .attr("font-family", "Georgia")
	    .attr("font-size", function(){
	        return ((Math.log(value) / Math.log(1.175)) + "px") 
	    })
	    .text(rightKeyFrames[month][1][i].word)
	    .attr("fill", function(){
	        return R_color_scale(value)
	    })
	    .attr("text-anchor", "middle")
	}
    
    //CHANGE DATE AT BOTTOM
    date
        .text(formatDate(rightKeyFrames[month][0]))
        .attr("text-align", "center")
}

