//LOAD AND PROCESS DATA
function loadData() {
	leftData = leftjsonData;
	rightData = rightjsonData;

	//PARSE DATE
	var i;
	for (i of leftData) {
		i.date = new Date(i.date);
	}

	for(i of rightData) {
		i.date = new Date(i.date);
	}

	//GROUP BY WORDS
	d3.group(leftData, d => d.word)
	d3.group(rightData, d => d.word)

	//CREATE SET OF WORDS
	leftWords = new Set(leftData.map(d => d.word))
	rightWords = new Set(rightData.map(d => d.word))
	

  	//GROUP BY DATE
	leftDateValues = Array.from(d3.rollup(leftData, ([d]) => d.value, d => +d.date, d => d.word))
	.map(([date, leftData]) => [new Date(date), leftData])
	.sort(([a], [b]) => d3.ascending(a, b))

	rightDateValues = Array.from(d3.rollup(rightData, ([d]) => d.value, d => +d.date, d => d.word))
	.map(([date, rightData]) => [new Date(date), rightData])
	.sort(([a], [b]) => d3.ascending(a, b))

	//FIND RANK OF EACH DATE

	//num words to return for each date (we want top 10)
	var n = 10;

	function leftRank(value) {
		const data = Array.from(leftWords, word => ({word, value: value(word) || 0}));
		data.sort((a, b) => d3.descending(a.value, b.value));
		for (let i = 0; i < data.length; ++i) data[i].rank = Math.min(n, i);
		return data;
	}

	function rightRank(value) {
		const data = Array.from(rightWords, word => ({word, value: value(word) || 0}));
		data.sort((a, b) => d3.descending(a.value, b.value));
		for (let i = 0; i < data.length; ++i) data[i].rank = Math.min(n, i);
		return data;
	}

	//CREATE KEY FRAMES FOR ANIMATION
	var k = 1;

	leftKeyFrames = [];
		let ka, a;
		for ([ka, a] of leftDateValues) {
			leftKeyFrames.push([
				new Date(ka), leftRank(word => a.get(word))])
		}

    rightKeyFrames = [];
        let ja, b;
        for ([ja, b] of rightDateValues) {
            rightKeyFrames.push([
                new Date(ja), rightRank(word => b.get(word))])
        }   

	// //PREPARE FOR ENTERING AND EXITING (ANIMATION)
	var nameframes = d3.groups(leftKeyFrames.flatMap(([, data]) => data), d => d.word)
	prev = new Map(nameframes.flatMap(([, data]) => d3.pairs(data, (a, b) => [b, a])))
	next = new Map(nameframes.flatMap(([, data]) => d3.pairs(data)))
}